# ====================== BOT FUNCTIONS ======================

# 1. START COMMAND
@app.on_message(filters.command("start"))
async def start(client, message):
    keyboard = InlineKeyboardMarkup([
        [InlineKeyboardButton("📌 Join Course", callback_data="join_course")]
    ])
    await message.reply(
        "🎉 Welcome to HSTrader Free Course!\n"
        "Click the button below to join.",
        reply_markup=keyboard
    )

# 2. JOIN COURSE BUTTON
@app.on_callback_query(filters.regex("join_course"))
async def join_course(client, callback):
    await callback.message.edit_text(
        "📢 To join:\n"
        "1️⃣ Register via our link:\n"
        "https://po-ru4.click/register?utm_campaign=820621...\n\n"
        "2️⃣ Send your Pocket Option ID (numbers only)."
    )

# 3. PO ID VERIFICATION
@app.on_message(filters.text & ~filters.command("start"))
async def verify_po_id(client, message):
    if not message.text.isdigit():
        await message.reply("❌ Only numbers allowed (e.g., 123456)")
        return

    po_id = message.text
    user_id = message.from_user.id

    # Duplicate check
    cursor.execute("SELECT * FROM users WHERE po_id=?", (po_id,))
    if cursor.fetchone():
        await message.reply("⚠️ This ID is already registered!")
        return

    # Send to Pocket Option bot
    await app.send_message(po_bot_username, f"{po_check_command} {po_id}")
    await message.reply("🔍 Verifying your ID...")

# 4. HANDLE POCKET OPTION BOT'S RESPONSE
@app.on_message(filters.user(po_bot_username))
async def handle_po_response(client, message):
    reply_to = message.reply_to_message
    if not reply_to or not reply_to.text.startswith(po_check_command):
        return

    user_id = reply_to.from_user.id
    response = message.text

    try:
        # Extract balance
        balance_line = [line for line in response.split('\n') if "Balance:" in line][0]
        balance = float(balance_line.split("$")[1].strip())
        
        # Save to DB
        cursor.execute(
            "INSERT OR REPLACE INTO users VALUES (?, ?, ?, ?, ?)",
            (user_id, po_id, balance, 0, datetime.now().strftime("%Y-%m-%d"))
        )
        conn.commit()

        if balance >= 50:
            await app.send_message(user_id, "✅ Verified! Group link: [YOUR_GROUP_LINK]")
        else:
            await app.send_message(
                user_id,
                f"❌ Minimum $50 required (Current: ${balance}). Deposit and resend PO ID."
            )
    except Exception as e:
        print("Error:", e)
        await app.send_message(user_id, "⚠️ Invalid PO ID. Use our referral link!")

# ====================== AUTO-CHECK SYSTEM ======================

# 5. DAILY CHECK (11PM)
async def daily_check():
    print("Running daily check...")
    cursor.execute("SELECT user_id, po_id FROM users")
    for user_id, po_id in cursor.fetchall():
        await app.send_message(po_bot_username, f"{po_check_command} {po_id}")

# 6. BALANCE CHECK LOGIC
async def check_balance(user_id, new_balance):
    cursor.execute("SELECT balance, zero_days FROM users WHERE user_id=?", (user_id,))
    result = cursor.fetchone()
    if not result:
        return

    old_balance, zero_days = result
    if new_balance == 0:
        zero_days += 1
        if zero_days >= 7:
            # Remove user
            await app.ban_chat_member(group_id, user_id)
            await app.send_message(user_id, "🚫 Removed: 7 days with $0 balance.")
            cursor.execute("DELETE FROM users WHERE user_id=?", (user_id,))
        else:
            await app.send_message(
                user_id,
                f"⚠️ Your balance is $0. Days left: {7-zero_days}"
            )
        cursor.execute(
            "UPDATE users SET zero_days=? WHERE user_id=?",
            (zero_days, user_id)
        )
    elif new_balance > 0 and zero_days > 0:
        cursor.execute(
            "UPDATE users SET zero_days=0 WHERE user_id=?",
            (user_id,)
        )
    conn.commit()

# ====================== START BOT ======================
scheduler.add_job(daily_check, "cron", hour=23)  # 11PM daily
scheduler.start()
app.run()
