import asyncio
import os
import sqlite3
from pyrogram import Client, filters
from pyrogram.types import InlineKeyboardMarkup, InlineKeyboardButton
from pyrogram.enums import ParseMode  # NEW: Required for Pyrogram 3.0+
from apscheduler.schedulers.asyncio import AsyncIOScheduler
from datetime import datetime

# ================= CONFIG =================
api_id = int(os.getenv("API_ID", "29676661")) 
api_hash = os.getenv("API_HASH", "1b8fb46782cc52ced8510087c12859b3")
bot_token = os.getenv("BOT_TOKEN", "7771643202:AAFPydfvyOhe0XMSnNi-Z9U1Z78mhJ1Rmko")
admin_id = int(os.getenv("ADMIN_ID", "7765768262"))  # Your Telegram ID
group_id = int(os.getenv("GROUP_ID", "-1002655615295"))  # Your group ID
session_string = os.getenv("")  # From generate_session.py
po_bot_username = "@AffiliatePocketBot"  # Change if different

# ============== BOT SETUP ==============
app = Client(
    name="hs_bot",
    api_id=api_id,
    api_hash=api_hash,
    bot_token=bot_token,
    session_string=session_string,
    in_memory=True  # Better session handling
)

# ============ DATABASE SETUP ============
conn = sqlite3.connect('database.db', check_same_thread=False)
cursor = conn.cursor()
cursor.execute('''CREATE TABLE IF NOT EXISTS users
                (user_id INTEGER PRIMARY KEY, 
                po_id TEXT UNIQUE, 
                balance REAL,
                zero_days INTEGER DEFAULT 0,
                join_date TEXT)''')
conn.commit()

# ============ GLOBAL TRACKERS ============
pending_verifications = {}  # Tracks ongoing verifications

# ============ SCHEDULER ============
scheduler = AsyncIOScheduler()

# ============== HANDLERS ==============
@app.on_message(filters.command("start"))
async def start(client, message):
    keyboard = InlineKeyboardMarkup([
        [InlineKeyboardButton("📌 Join Course", callback_data="join_course")]
    ])
    await message.reply(
        text="🎉 Welcome to HSTrader Free Course!\nClick below to join:",
        reply_markup=keyboard,
        parse_mode=ParseMode.MARKDOWN  # NEW: Proper parse mode
    )

@app.on_callback_query(filters.regex("join_course"))
async def join_course(client, callback):
    await callback.message.edit_text(
        text="🔗 Register via our link:\nhttps://po-ru4.click/register\n\n"
             "📝 Then send your Pocket Option ID (numbers only):",
        parse_mode=ParseMode.MARKDOWN
    )

@app.on_message(filters.text & ~filters.command("start"))
async def verify_po_id(client, message):
    if not message.text.isdigit():
        await message.reply(
            text="❌ Only numbers allowed!",
            parse_mode=ParseMode.MARKDOWN
        )
        return

    po_id = message.text
    user_id = message.from_user.id

    # Duplicate check
    cursor.execute("SELECT 1 FROM users WHERE po_id=?", (po_id,))
    if cursor.fetchone():
        await message.reply(
            text="⚠️ This ID already exists!",
            parse_mode=ParseMode.MARKDOWN
        )
        return

    # Store verification request
    sent_msg = await app.send_message(
        chat_id=po_bot_username,
        text=f"/user {po_id}"
    )
    pending_verifications[user_id] = {
        'po_id': po_id,
        'sent_msg_id': sent_msg.id
    }
    await message.reply(
        text="🔍 Verification started...",
        parse_mode=ParseMode.MARKDOWN
    )

@app.on_message(filters.user(po_bot_username) & filters.reply)
async def handle_po_response(client, message):
    replied_msg_id = message.reply_to_message.id
    
    # Find matching verification request
    user_id = next(
        (uid for uid, data in pending_verifications.items() 
         if data['sent_msg_id'] == replied_msg_id),
        None
    )
    
    if not user_id:
        return

    try:
        # Extract balance
        balance_line = next(
            line for line in message.text.split('\n') 
            if "Balance:" in line
        )
        balance = float(balance_line.split("$")[1].strip())

        # Save to DB
        cursor.execute(
            """INSERT OR REPLACE INTO users 
            (user_id, po_id, balance, join_date) 
            VALUES (?, ?, ?, ?)""",
            (user_id, pending_verifications[user_id]['po_id'], 
             balance, datetime.now().strftime("%Y-%m-%d"))
        )
        conn.commit()

        # Respond to user
        if balance >= 50:
            response = "✅ Approved! Group link: [YOUR_GROUP_LINK]"
        else:
            response = f"❌ Minimum $50 required (Current: ${balance})"

        await app.send_message(
            chat_id=user_id,
            text=response,
            parse_mode=ParseMode.MARKDOWN
        )

    except Exception as e:
        print(f"Error: {e}")
        await app.send_message(
            chat_id=user_id,
            text="⚠️ Verification failed. Please try again.",
            parse_mode=ParseMode.MARKDOWN
        )
    finally:
        # Cleanup
        pending_verifications.pop(user_id, None)

# ============ DAILY CHECK ============
async def daily_check():
    print("🔁 Running daily checks...")
    cursor.execute("SELECT user_id, po_id FROM users")
    for user_id, po_id in cursor.fetchall():
        await app.send_message(
            chat_id=po_bot_username,
            text=f"/user {po_id}"
        )

# ============ START BOT ============
async def run_bot():
    scheduler.add_job(daily_check, "cron", hour=23)
    scheduler.start()
    print("🤖 Bot started successfully!")
    await app.run()

if __name__ == "__main__":
    asyncio.run(run_bot())
