# ========== SQLite Setup ==========

conn = sqlite3.connect("users.db", check_same_thread=False)
cursor = conn.cursor()

cursor.execute("""
CREATE TABLE IF NOT EXISTS users (
    telegram_id INTEGER PRIMARY KEY,
    po_id TEXT UNIQUE,
    balance INTEGER,
    joining_date TEXT
)
""")
conn.commit()

# ========== Helpers ==========

def add_user(telegram_id, po_id, balance):
    joining_date = datetime.utcnow().strftime("%Y-%m-%d %H:%M:%S")
    try:
        cursor.execute(
            "INSERT INTO users (telegram_id, po_id, balance, joining_date) VALUES (?, ?, ?, ?)",
            (telegram_id, po_id, balance, joining_date)
        )
        conn.commit()
        return True
    except sqlite3.IntegrityError:
        return False  # duplicate telegram_id or po_id

def update_balance(telegram_id, new_balance):
    cursor.execute(
        "UPDATE users SET balance = ? WHERE telegram_id = ?",
        (new_balance, telegram_id)
    )
    conn.commit()

def get_user_by_po_id(po_id):
    cursor.execute("SELECT telegram_id FROM users WHERE po_id = ?", (po_id,))
    result = cursor.fetchone()
    return result[0] if result else None

def get_all_users():
    cursor.execute("SELECT telegram_id, po_id, balance FROM users")
    return cursor.fetchall()

def user_exists(telegram_id):
    cursor.execute("SELECT 1 FROM users WHERE telegram_id = ?", (telegram_id,))
    return cursor.fetchone() is not None

