import asyncio
import os
import sqlite3
from pyrogram import Client, filters
from pyrogram.types import InlineKeyboardMarkup, InlineKeyboardButton
from apscheduler.schedulers.asyncio import AsyncIOScheduler
from datetime import datetime

# ================= CONFIG =================
# ================= CONFIG =================
api_id = int(os.getenv("API_ID", "29676661"))  # Fallback to your ID
api_hash = os.getenv("API_HASH", "1b8fb46782cc52ced8510087c12859b3") 
bot_token = os.getenv("BOT_TOKEN", "7802794930:AAGNO7IK_DB5chmCruiXOmfIPdSW_Phet4g")
admin_id = int(os.getenv("ADMIN_ID", "7765768262"))
group_id = int(os.getenv("GROUP_ID", "-1002655615295"))
session_string = os.getenv("")  # From generate_session.py
po_bot_username = "@AffiliatePocketBot"  # Change if different

# ============== BOT SETUP ==============
app = Client(
    "hs_bot",
    api_id=api_id,
    api_hash=api_hash,
    bot_token=bot_token,
    session_string=session_string
)

# ============ DATABASE SETUP ============
conn = sqlite3.connect('database.db', check_same_thread=False)
cursor = conn.cursor()
cursor.execute('''CREATE TABLE IF NOT EXISTS users
                (user_id INTEGER PRIMARY KEY, 
                po_id TEXT UNIQUE, 
                balance REAL,
                zero_days INTEGER DEFAULT 0,
                join_date TEXT)''')
conn.commit()

# ============ SCHEDULER ============
scheduler = AsyncIOScheduler()

# ============== HANDLERS ==============
@app.on_message(filters.command("start"))
async def start(client, message):
    keyboard = InlineKeyboardMarkup([
        [InlineKeyboardButton("📌 Join Course", callback_data="join_course")]
    ])
    await message.reply(
        "🎉 Welcome to HSTrader Free Course!\n"
        "Click below to join:",
        reply_markup=keyboard
    )

@app.on_callback_query(filters.regex("join_course"))
async def join_course(client, callback):
    await callback.message.edit_text(
        "🔗 Register via our link:\n"
        "https://po-ru4.click/register\n\n"
        "📝 Then send your Pocket Option ID (numbers only):"
    )

@app.on_message(filters.text & ~filters.command("start"))
async def verify_po_id(client, message):
    if not message.text.isdigit():
        await message.reply("❌ Only numbers allowed!")
        return

    po_id = message.text
    user_id = message.from_user.id

    # Duplicate check
    cursor.execute("SELECT 1 FROM users WHERE po_id=?", (po_id,))
    if cursor.fetchone():
        await message.reply("⚠️ This ID already exists!")
        return

    # Forward to PO bot
    await app.send_message(po_bot_username, f"/user {po_id}")
    await message.reply("🔍 Verifying...")

@app.on_message(filters.user(po_bot_username))
async def handle_po_response(client, message):
    try:
        # Extract info from PO bot's response
        response = message.text
        user_id = message.reply_to_message.from_user.id
        balance = float(next(
            line.split("$")[1].strip() 
            for line in response.split('\n') 
            if "Balance:" in line
        ))

        # Save to DB (FIXED: Added missing parenthesis)
        cursor.execute(
            """INSERT INTO users 
            (user_id, po_id, balance, join_date) 
            VALUES (?, ?, ?, ?)""",
            (user_id, po_id, balance, datetime.now().strftime("%Y-%m-%d"))
        )
        conn.commit()

        # Respond to user
        if balance >= 50:
            await app.send_message(user_id, "✅ Approved! Group link: [LINK]")
        else:
            await app.send_message(
                user_id,
                f"❌ Minimum $50 required (Current: ${balance})"
            )
    except Exception as e:
        print(f"Error: {e}")
        await app.send_message(user_id, "⚠️ Verification failed")

# ============ AUTO-CHECKS ============
async def daily_check():
    print("🔁 Running daily checks...")
    cursor.execute("SELECT user_id, po_id FROM users")
    for user_id, po_id in cursor.fetchall():
        await app.send_message(po_bot_username, f"/user {po_id}")

async def startup():
    scheduler.add_job(daily_check, "cron", hour=23)  # 11PM daily
    scheduler.start()
    print("🤖 Bot started successfully!")

# ============ MAIN ============
async def run_bot():
    await startup()
    await app.run()

if __name__ == "__main__":
    asyncio.run(run_bot())
