import asyncio
import os
import sqlite3
from dotenv import load_dotenv
from pyrogram import Client, filters
from pyrogram.types import InlineKeyboardMarkup, InlineKeyboardButton
from datetime import datetime

# Load .env variables
load_dotenv()

# Get config from .env
API_ID = int(os.getenv("API_ID", "29676661"))
API_HASH = os.getenv("1b8fb46782cc52ced8510087c12859b3")
BOT_TOKEN = os.getenv("7771643202:AAFPydfvyOhe0XMSnNi-Z9U1Z78mhJ1Rmko")
SESSION_STRING = os.getenv("")
ADMIN_ID = int(os.getenv("7765768262"))
GROUP_ID = int(os.getenv("-1002655615295"))
PO_BOT_USERNAME = "@AffiliatePocketBot"

# DB setup
conn = sqlite3.connect('database.db', check_same_thread=False)
cursor = conn.cursor()
cursor.execute('''CREATE TABLE IF NOT EXISTS users
                (user_id INTEGER PRIMARY KEY, 
                po_id TEXT UNIQUE, 
                balance REAL,
                join_date TEXT)''')
conn.commit()

# Clients
bot_client = Client("bot", api_id=API_ID, api_hash=API_HASH, bot_token=BOT_TOKEN)
user_client = Client("user", api_id=API_ID, api_hash=API_HASH, session_string=SESSION_STRING)

pending_verifications = {}

@bot_client.on_message(filters.command("start"))
async def start(client, message):
    keyboard = InlineKeyboardMarkup([
        [InlineKeyboardButton("📌 Join Course", callback_data="join_course")]
    ])
    await message.reply(
        "🎉 Welcome to HSTrader Free Course!\nClick below to join:",
        reply_markup=keyboard
    )

@bot_client.on_callback_query(filters.regex("join_course"))
async def join_course(client, callback):
    await callback.message.edit_text(
        "🔗 Register via our link:\n"
        "https://po-ru4.click/register\n\n"
        "📝 Then send your Pocket Option ID (numbers only):"
    )

@bot_client.on_message(filters.text & ~filters.command("start"))
async def get_po_id(client, message):
    if not message.text.isdigit():
        await message.reply("❌ Only numbers allowed!")
        return

    po_id = message.text
    user_id = message.from_user.id

    cursor.execute("SELECT 1 FROM users WHERE po_id=?", (po_id,))
    if cursor.fetchone():
        await message.reply("⚠️ This ID already exists!")
        return

    # Save temporarily for linking reply later
    pending_verifications[po_id] = user_id

    await message.reply("🔍 Verifying your Pocket Option ID, please wait...")

    # Send /user <po_id> to Pocket Bot via user account
    await user_client.send_message(PO_BOT_USERNAME, f"/user {po_id}")

@user_client.on_message(filters.private & filters.chat(PO_BOT_USERNAME))
async def handle_po_reply(client, message):
    text = message.text

    try:
        # Extract PO ID from message (assuming /user 12345 was sent)
        po_id_line = next(line for line in text.split('\n') if "ID:" in line)
        po_id = po_id_line.split("ID:")[1].strip()

        # Get original user ID who sent it
        user_id = pending_verifications.get(po_id)
        if not user_id:
            return

        # Get balance
        balance_line = next(line for line in text.split('\n') if "Balance:" in line)
        balance = float(balance_line.split("$")[1].strip())

        # Save to database
        cursor.execute(
            "INSERT INTO users (user_id, po_id, balance, join_date) VALUES (?, ?, ?, ?)",
            (user_id, po_id, balance, datetime.now().strftime("%Y-%m-%d"))
        )
        conn.commit()

        if balance >= 50:
            await bot_client.send_message(user_id, f"✅ Verified! Your balance is ${balance}. Join our group: https://t.me/+abc123xyz")
        else:
            await bot_client.send_message(user_id, f"❌ You need $50 minimum balance. Your balance is ${balance}.")

        del pending_verifications[po_id]

    except Exception as e:
        print("Error verifying:", e)
