import asyncio
import os
import sqlite3
from datetime import datetime
from pyrogram import Client, filters
from pyrogram.types import InlineKeyboardMarkup, InlineKeyboardButton
from apscheduler.schedulers.asyncio import AsyncIOScheduler

# Get environment variables
API_ID = int(os.getenv("29676661"))
API_HASH = os.getenv("1b8fb46782cc52ced8510087c12859b3")
BOT_TOKEN = os.getenv("7771643202:AAFPydfvyOhe0XMSnNi-Z9U1Z78mhJ1Rmko")
ADMIN_ID = int(os.getenv("7765768262"))
GROUP_ID = int(os.getenv("-1002655615295"))
SESSION_STRING = os.getenv("")

PO_BOT_USERNAME = "@AffiliatePocketBot"

# Database setup
conn = sqlite3.connect("database.db", check_same_thread=False)
cursor = conn.cursor()
cursor.execute('''CREATE TABLE IF NOT EXISTS users
                (user_id INTEGER PRIMARY KEY,
                po_id TEXT UNIQUE,
                balance REAL,
                zero_days INTEGER DEFAULT 0,
                join_date TEXT)''')
conn.commit()

app = Client("hs_bot", api_id=API_ID, api_hash=API_HASH, bot_token=BOT_TOKEN, session_string=SESSION_STRING)
scheduler = AsyncIOScheduler()

@app.on_message(filters.command("start"))
async def start(client, message):
    keyboard = InlineKeyboardMarkup([
        [InlineKeyboardButton("📌 Join Course", callback_data="join_course")]
    ])
    await message.reply(
        "🎉 Welcome to HSTrader Free Course!\nClick below to join:",
        reply_markup=keyboard
    )

@app.on_callback_query(filters.regex("join_course"))
async def join_course(client, callback):
    await callback.message.edit_text(
        "🔗 Register via our link:\n"
        "https://po-ru4.click/register\n\n"
        "📝 Then send your Pocket Option ID (numbers only):"
    )

@app.on_message(filters.text & ~filters.command("start"))
async def verify_po_id(client, message):
    if not message.text.isdigit():
        await message.reply("❌ Only numbers allowed!")
        return

    po_id = message.text
    user_id = message.from_user.id

    cursor.execute("SELECT 1 FROM users WHERE po_id=?", (po_id,))
    if cursor.fetchone():
        await message.reply("⚠️ This ID already exists!")
        return

    await app.send_message(PO_BOT_USERNAME, f"/user {po_id}")
    await message.reply("🔍 Verifying...")

    # Temporarily save po_id linked to user_id for response processing
    cursor.execute("INSERT OR REPLACE INTO users (user_id, po_id) VALUES (?, ?)", (user_id, po_id))
    conn.commit()

@app.on_message(filters.user(PO_BOT_USERNAME))
async def handle_po_response(client, message):
    try:
        response = message.text
        lines = response.split("\n")
        po_id = None
        balance = 0.0

        for line in lines:
            if "Balance:" in line:
                balance = float(line.split("$")[1].strip())

        # Find user_id based on last inserted po_id
        cursor.execute("SELECT user_id FROM users WHERE po_id IS NOT NULL ORDER BY ROWID DESC LIMIT 1")
        row = cursor.fetchone()
        if not row:
            return
        user_id = row[0]

        cursor.execute("UPDATE users SET balance=?, join_date=? WHERE user_id=?",
                       (balance, datetime.now().strftime("%Y-%m-%d"), user_id))
        conn.commit()

        if balance >= 50:
            await app.send_message(user_id, "✅ Approved! Group link: https://t.me/+YOUR_GROUP_LINK")
        else:
            await app.send_message(user_id, f"❌ Minimum $50 required (Current: ${balance:.2f})")

    except Exception as e:
        print(f"Error handling PO response: {e}")

# Daily check (optional)
async def daily_check():
    print("🔁 Running daily checks...")
    cursor.execute("SELECT user_id, po_id FROM users")
    for user_id, po_id in cursor.fetchall():
        await app.send_message(PO_BOT_USERNAME, f"/user {po_id}")

async def main():
    scheduler.add_job(daily_check, "cron", hour=23)
    scheduler.start()
    await app.start()
    print("🤖 Bot started successfully!")
    while True:
        await asyncio.sleep(3600)

if __name__ == "__main__":
    asyncio.run(main())
