@app.on_message(filters.text & ~filters.command("start"))
async def verify_po_id(client, message):
    if not message.text.isdigit():
        await message.reply("❌ Only numbers allowed (e.g., 123456)")
        return

    po_id = message.text
    user_id = message.from_user.id

    # Check if ID already registered by any user
    cursor.execute("SELECT user_id FROM users WHERE po_id=?", (po_id,))
    row = cursor.fetchone()
    if row and row[0] != user_id:
        await message.reply("⚠️ This Pocket Option ID is already registered by another user.")
        return

    # Save pending verification for this user
    pending_verifications[user_id] = po_id

    # Send to Pocket Option bot for verification
    await app.send_message(po_bot_username, f"{po_check_command} {po_id}")
    await message.reply("🔍 Verifying your Pocket Option ID... Please wait.")

@app.on_message(filters.user(po_bot_username))
async def handle_po_response(client, message):
    # Find the original user who requested this verification
    # We'll get it from the pending_verifications dict by matching ID in the message text

    text = message.text or ""
    # Extract UID line
    uid_line = next((line for line in text.split('\n') if line.startswith("UID:")), None)

    if uid_line:
        po_id = uid_line.split(":")[1].strip()
    elif "user not found" in text.lower():
        # If user not found, send message to all pending users with no match? 
        # Safer: match user_id who is pending this ID from pending_verifications
        # But we can't guess user_id here, so let's scan all pending_verifications and remove ones that match the po_id
        # This is a limitation; better to store a queue with msg_id mapping (advanced)
        # For now, just ignore if no UID found
        po_id = None
    else:
        # No UID, ignore
        return

    if not po_id:
        return

    # Find user_id with this po_id in pending_verifications
    matched_users = [uid for uid, pid in pending_verifications.items() if pid == po_id]

    if not matched_users:
        # No user waiting for this PO ID, ignore
        return

    user_id = matched_users[0]

    # Remove from pending list
    pending_verifications.pop(user_id, None)

    if "user not found" in text.lower():
        await app.send_message(user_id, "❌ Pocket Option ID not found or invalid. Please register with our referral link and try again.")
        return

    try:
        # Extract balance
        balance_line = next(line for line in text.split('\n') if "Balance:" in line)
        balance = float(balance_line.split("$")[1].strip())

        # Insert or update in DB
        cursor.execute(
            "INSERT OR REPLACE INTO users (user_id, po_id, balance, zero_days, join_date) VALUES (?, ?, ?, COALESCE((SELECT zero_days FROM users WHERE user_id=?), 0), COALESCE((SELECT join_date FROM users WHERE user_id=?), ?))",
            (user_id, po_id, balance, user_id, user_id, datetime.now().strftime("%Y-%m-%d"))
        )
        conn.commit()

        if balance >= 50:
            await app.send_message(user_id, "✅ Verified! You meet the $50 deposit requirement. Here is your group invite link: [YOUR_GROUP_LINK]")
        else:
            await app.send_message(
                user_id,
                f"❌ Your balance is ${balance}. Please deposit at least $50 and resend your Pocket Option ID."
            )
    except Exception as e:
        print("Error parsing PO response:", e)
        await app.send_message(user_id, "⚠️ Error verifying your Pocket Option ID. Please try again.")

async def daily_check():
    print(f"{datetime.now()}: Running daily check...")
    cursor.execute("SELECT user_id, po_id, zero_days FROM users")
    rows = cursor.fetchall()
    for user_id, po_id, zero_days in rows:
        await app.send_message(po_bot_username, f"{po_check_command} {po_id}")

        # We will need to wait for PO bot's response to update balance
        # But as we don't have a synchronous way, we rely on the handle_po_response to update balances

        # After some time, you can implement a system to track balance updates
        # For now, just send the request; user balances update asynchronously.

# Reset zero days logic will be handled inside handle_po_response by updating DB

def start_scheduler():
    loop = asyncio.get_event_loop()
    scheduler.add_job(daily_check, "cron", hour=23, minute=0)
    scheduler.start()

if __name__ == "__main__":
    start_scheduler()
    app.run()
